    private void CalculateButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CalculateButton1ActionPerformed
        this.Num1TF.setText(null);
        this.Num2TF.setText(null);
        this.Num3TF.setText(null);
        this.Num4TF.setText(null);
        this.answerText.setText(null);
        this.chisquareTF.setText(null);
        long number1, number2, number3, number4, number5, number6;
        try {
            number1 = Long.parseLong(this.numTF1.getText());
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "Bad first number", "Error", JOptionPane.ERROR_MESSAGE);
            return;
        }
        try {
            number2 = Long.parseLong(this.numTF2.getText());
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "Bad second number", "Error", JOptionPane.ERROR_MESSAGE);
            return;
        }
        try {
            number3 = Long.parseLong(this.numTF3.getText());
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "Bad third number", "Error", JOptionPane.ERROR_MESSAGE);
            return;
        }
        try {
            number4 = Long.parseLong(this.numTF4.getText());
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "Bad fourth number", "Error", JOptionPane.ERROR_MESSAGE);
            return;
        }
        try {
            number5 = Long.parseLong(this.numTF5.getText());
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "Bad fifth number", "Error", JOptionPane.ERROR_MESSAGE);
            return;
        }
        try {
            number6 = Long.parseLong(this.numTF6.getText());
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "Bad sixth number", "Error", JOptionPane.ERROR_MESSAGE);
            return;
        }
        long[][] obs = new long[2][3];

        obs[0][0] = number1;
        obs[0][1] = number2;
        obs[0][2] = number3;
        obs[1][0] = number4;
        obs[1][1] = number5;
        obs[1][2] = number6;

        ChiSquareTest mychiSquare = new ChiSquareTest();

        double stats =  mychiSquare.chiSquare(obs);
        this.chisquareTF.setText(String.valueOf(stats));

        double pvalue = mychiSquare.chiSquareTest(obs);
        this.answerText.setText(String.valueOf(pvalue));   
    }//GEN-LAST:event_CalculateButton1ActionPerformed

