    private void CalculateButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CalculateButtonActionPerformed
        long number1, number2, number3, number4;
        this.numTF1.setText(null);
        this.numTF2.setText(null);
        this.numTF3.setText(null);
        this.numTF4.setText(null);
        this.numTF5.setText(null);
        this.numTF6.setText(null);
        this.answerText.setText(null);
        this.chisquareTF.setText(null);
        try {
            number1 = Long.parseLong( this.Num1TF.getText());
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "Bad first number", "Error", JOptionPane.ERROR_MESSAGE);
            return;
        }

        try {
            number2 = Long.parseLong(this.Num2TF.getText());
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "Bad second number", "Error", JOptionPane.ERROR_MESSAGE);
            return;
        }

        try {
            number3 = Long.parseLong(this.Num3TF.getText());
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "Bad third number", "Error", JOptionPane.ERROR_MESSAGE);
            return;
        }

        try {
            number4 = Long.parseLong(this.Num4TF.getText());
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "Bad fourth number", "Error", JOptionPane.ERROR_MESSAGE);
            return;
        }

        long[][] obs = new long[2][2];

        obs[0][0] = number1;
        obs[0][1] = number2;
        obs[1][0] = number3;
        obs[1][1] = number4;

        ChiSquareTest mychiSquare = new ChiSquareTest();

        double stats =  mychiSquare.chiSquare(obs);
        this.chisquareTF.setText(String.valueOf(stats));

        double pvalue = mychiSquare.chiSquareTest(obs);
        this.answerText.setText(String.valueOf(pvalue));
    }//GEN-LAST:event_CalculateButtonActionPerformed

